step.command:
$| = 1;

system("p4 clients");

print "-- Deleting p4 tmp clients\n";
my @clients = `p4 clients`;
foreach my $client (@clients){
  next unless $client =~ /(cmdr-tmp-client-\d*)/;
  $client = $1;
  print "> $client\n";
  next unless $client;
  !system("p4 client -d $client") or die "ERROR: Failed to delete client $client";
}

sleep 5;
